import { createSelector } from 'reselect';

const selectCollectionState = state => state.firebaseCollections;
export const selectCollections = createSelector(
    [selectCollectionState],
    collectionsState => collectionsState.collections
);

export const selectCollectionForPreview = createSelector(
    [selectCollections],
    firebaseCollections => firebaseCollections ? Object.keys(firebaseCollections).map(key => firebaseCollections[key]) : []
)


export function selectCollection(collectionUrlParam) {
    return createSelector(
        [selectCollections],
        firebaseCollections => firebaseCollections ? firebaseCollections[collectionUrlParam] : []
    );
}
