import styled, { css } from 'styled-components';

const largeStyle = css`
    height: 380px;
`;

const getLargeStyle = (props) => {
    if (props.large !== undefined) {
        return largeStyle;
    }
}

export const MenuItemContainer = styled.div`
    min-width: 30%;
    height: 240px;
    flex: 1 1 auto;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 1px solid black;
    margin: 0 7.5px 15px;
    font-family: 'Open Sans Condensed', sans-serif;
    overflow: hidden;

    .backgroundImage {
      width: 100%;
      height: 100%;
      background-size: cover;
      background-position: center;
      transition: 1.2s;
    }

    &:hover {
      cursor: pointer;

      & .backgroundImage {
        transform: scale(1.1);
        transition: transform 6s cubic-bezier(0.25, 0.45, 0.45, 0.95);
      }

      & .content {
        opacity: 0.9;
      }
    }

    &:first-child {
      margin-right: 7.5px;
    }

    &:last-child {
      margin-left: 7.5px;
    }

    .content {
      height: 90px;
      padding: 0 25px;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      border: 1px solid black;
      background-color: white;
      opacity: 0.7;
      transition: 0.3s;
      position: absolute;

      .title {
        font-weight: bold;
        margin-top: 0;
        margin-bottom: 6px;
        font-size: 22px;
        color: #4a4a4a;
      }

      .subtitle {
        font-weight: lighter;
        font-size: 16px;
      }
    }

    ${getLargeStyle}
`;
