import { ACTION_TYPES } from '../action-types';
export function setCurrentUser(user) {
    return {
        type: ACTION_TYPES.SET_CURRENT_USER,
        payload: user
    }
}

export function googleSignInStart() {
    return {
        type: ACTION_TYPES.GOOGLE_SIGN_IN_START
    }
}

export function googleSignInSuccess(user) {
    return {
        type: ACTION_TYPES.GOOGLE_SIGN_IN_SUCCESS,
        payload: user
    }
}

export function googleSignInFailure(error) {
    return {
        type: ACTION_TYPES.GOOGLE_SIGN_IN_FAILURE,
        payload: error
    }
}

export function emailSignInStart() {
    return {
        type: ACTION_TYPES.EMAIL_SIGN_IN_START
    }
}

export function emailSignInSuccess(emailAndPassword) {
    return {
        type: ACTION_TYPES.EMAIL_SIGN_IN_SUCCESS,
        payload: emailAndPassword
    }
}

export function emailSignInFailure(error) {
    return {
        type: ACTION_TYPES.EMAIL_SIGN_IN_FAILURE,
        payload: error
    }
}
