import { ACTION_TYPES } from './action-types';
const INITIAL_STATE = {
    cartIsHidden: false,
    items: []
}
function cartReducer(state = INITIAL_STATE, action) {
    switch (action.type) {
        case ACTION_TYPES.TOGGLE_CART_VISIBLE:
            return {
                ...state,
                cartIsHidden: !state.cartIsHidden
            }
        default:
            return state;
    }
}
export default cartReducer;
