import React from 'react';
import './checkoutPage.scss';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';
import { selectTotalCostInCart } from '../../redux/cart.selectors';

function CheckoutPage({ totalCost }) {
    return (
        <div className="checkoutPage">
            <div className="itemsHeader">
                <div className="headerBlock">
                    <span className="column">Product</span>
                </div>
                <div className="headerBlock">
                    <span className="column">Description</span>
                </div>
                <div className="headerBlock">
                    <span className="column">Quantity</span>
                </div>
                <div className="headerBlock">
                    <span className="column">Price</span>
                </div>
                <div className="headerBlock">
                    <span className="column">Remove</span>
                </div>
            </div>

            <div className="cartItems">

            </div>
            <div className="totalContainer">
                <span className="total">TOTAL: ${totalCost}</span>
            </div>
        </div>
    )
}

const mapStateToProps = createStructuredSelector(
    {
        totalCost: selectTotalCostInCart
    }
)
export default connect(null, mapStateToProps)(CheckoutPage);
