import { createSelector } from 'reselect';

const selectFirebaseCollectionState = state => state.firebaseCollections;
export const selectCollections = createSelector(
    [selectFirebaseCollectionState],
    firebaseCollectionsState => firebaseCollectionsState.collections
);

export const selectIsFetching = createSelector(
    [selectFirebaseCollectionState],
    firebaseCollectionsState => firebaseCollectionsState.collectionsIsFetching
);

export const selectErrorMsg = createSelector(
    [selectFirebaseCollectionState],
    firebaseCollectionsState => firebaseCollectionsState.errorMsg
);

export const selectCollectionForPreview = createSelector(
    [selectCollections],
    firebaseCollections => firebaseCollections ? Object.keys(firebaseCollections).map(key => firebaseCollections[key]) : []
)


export function selectCollection(collectionUrlParam) {
    return createSelector(
        [selectCollections],
        firebaseCollections => firebaseCollections ? firebaseCollections[collectionUrlParam] : []
    );
}
