import { ACTION_TYPES } from '../action-types';
//import { SHOP_DATA } from '../../assets';

const INITIAL_STATE = {
    collections: null
}

function shopDataReducer(state = INITIAL_STATE, action) {
    switch(action.type) {
        case ACTION_TYPES.GET_COLLECTIONS_FROM_DB:
            return {
                ...state,
                firebaseCollections: action.payload
            }
        default:
            return state
    }
}
export default shopDataReducer;
