import React from 'react';
import CollectionsOverview from './CollectionsOverview';
import CollectionPage from '../Collection-Page/CollectionPage';
import { Route } from 'react-router-dom';
import { firestore, convertCollectionSnapshotToMap } from '../../firebase/utils';
import { connect } from 'react-redux';
import { getFirebaseCollections } from '../../redux/shop-data/shop-data.actions';
import withSpinner from '../HOCs/withSpinner';

const CollectionsOverviewWithSpinner = withSpinner(CollectionsOverview);
const CollectionPageWithSpinner      = withSpinner(CollectionPage);


// ShopPage gets collections from our firebase db
class ShopPage extends React.Component {
    state = {
        loading: true
    }
    unsubscribeFromSnapShot = null;

    componentDidMount() {
        const { getFirebaseCollections } = this.props;
        const collectionsRef = firestore.collection('collections');
        this.unsubscribeFromSnapShot = collectionsRef.onSnapshot(async snapshot => {
            const collectionsMap = convertCollectionSnapshotToMap(snapshot);
            getFirebaseCollections(collectionsMap);
            this.setState({ loading: false })
        });
    }

    render() {
        const { match } = this.props;
        const { loading } = this.state;
        return (
            <div className="shopPageWrapper">
                <Route exact path={match.path}
                    render={props => <CollectionsOverviewWithSpinner loading={loading} {...props}/>}
                />
                <Route path={`${match.path}/:collectionId`} 
                    render={props => <CollectionPageWithSpinner loading={loading} {...props}/>}
                />
            </div>
        )
    }
}

function mapDispatchToProps(dispatch) {
    return {
        getFirebaseCollections: function(collectionsMap) {
            return dispatch(getFirebaseCollections(collectionsMap));
        } 
    }
}
export default connect(null, mapDispatchToProps)(ShopPage);
