import { updateCollections } from './shop.actions';
import { ACTION_TYPES } from '../action-types';

const INITIAL_STATE = {
    collections: null
}

function updateCollectionsReducer(state = INITIAL_STATE, action) {
    switch(action.type) {
        case ACTION_TYPES.UPDATE_COLLECTIONS:
            return {
                ...state,
                collections: action.payload
            }
        default:
            return state
    }
}
export default updateCollectionsReducer;
