import React from 'react';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';
import { selectSections, selectSectionsAreLoading } from '../../redux/_db/home-page-sections-data/sections-data.selectors';
import MenuItem from './MenuItem';
import styles from './Menu.module.scss';

function Menu({ sectionsAreLoading, sections }) {

    return (
        <div className={styles.directoryMenu}>
            {   // title, imageUrl, id, linkUrl
                sectionsAreLoading ?
                sections.map(({ id, ...sectionProps }) => (
                    <MenuItem key={id} {...sectionProps} />
                )) : (<div>Loading</div>)
            }
        </div>
    );
}

const mapStateToProps = createStructuredSelector(
    {
        sections: selectSections,
        sectionsAreLoading: selectSectionsAreLoading
    }
)


export default connect(mapStateToProps)(Menu);
