import React from 'react';
import styles from './Homepage.module.scss';
import Menu from './Menu';
import { connect } from 'react-redux';
import { firestore } from '../../firebase/utils';
import { getFirebaseDirectoryCollection } from '../../redux/directory-data/directory.actions';
import { convertSectionsSnapshotToMap } from '../../firebase/utils';
import { createStructuredSelector } from 'reselect';

class HomePage extends React.Component {
    unsubscribeFromSnapshot = null;

    componentDidMount() {
        const { getFirebaseDirectoryCollection } = this.props;
        const homePageSectionsRef = firestore.collection('homePageSections');
        this.unsubscribeFromSnapshot = homePageSectionsRef.onSnapshot(async snapshot => {
            getFirebaseDirectoryCollection(snapshot.docs.map( doc => {
                return doc.data();
            }));
        })
    }

    render() {
        return (
            <div className={styles.homepage}>
                <Menu/>
            </div>
        );
    }
}

function mapDispatchToProps(dispatch) {
    return {
        getFirebaseDirectoryCollection: function(collectionsMap) {
            return dispatch(getFirebaseDirectoryCollection(collectionsMap));
        }
    }
}

export default connect(null, mapDispatchToProps)(HomePage);
