import React from 'react';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';
import { selectDirectorySections } from '../../redux/directory-data/directory.selectors'
import { getFirebaseDirectoryCollection } from '../../redux/directory-data/directory.actions';
import { firestore } from '../../firebase/utils';
import MenuItem from './MenuItem';
import styles from './Menu.module.scss';

class Menu extends React.Component {

    unsubscribeFromSnapshot = null;

    componentDidMount() {
        const { getFirebaseDirectoryCollection } = this.props;
        const homePageSectionsRef = firestore.collection('homePageSections');
        this.unsubscribeFromSnapshot = homePageSectionsRef.onSnapshot(async snapshot => {
            getFirebaseDirectoryCollection(snapshot.docs.map( doc => {
                return doc.data();
            }));
        })
    }

    render() {
        const { sections } = this.props;
        return (
            <div className={styles.directoryMenu}>
                {   // title, imageUrl, id, linkUrl
                    sections.map(({ id, ...sectionProps }) => (
                        <MenuItem key={id} {...sectionProps} />
                    ))
                }
            </div>
        );
    }
}

function mapDispatchToProps(dispatch) {
    return {
        getFirebaseDirectoryCollection: function(collectionsMap) {
            return dispatch(getFirebaseDirectoryCollection(collectionsMap));
        }
    }
}

const mapStateToProps = createStructuredSelector(
    {
        sections: selectDirectorySections
    }
)


export default connect(mapStateToProps, mapDispatchToProps)(Menu);
