import { all, call } from 'redux-saga/effects';
import { fetchCollectionsStart } from './_db/collections-data/collections-data.sagas';
import { fetchHomePageSectionsStart } from './_db/home-page-sections-data/sections-data.sagas';
import { userSagas } from './user/user.sagas';
export default function* rootSaga() {
    yield all([
        call(fetchCollectionsStart),
        call(fetchHomePageSectionsStart),
        call(userSagas)
    ]);
}
