import { createSelector } from 'reselect';

const selectFirebaseDirectory = state => state.firebaseDirectory;
export const selectDirectory = createSelector(
    [selectFirebaseDirectory],
    directoryState => directoryState.sections
);

export const selectDirectorySections = createSelector(
    [selectDirectory],
    sections => sections ? sections.map((obj, idx) => sections[sections.findIndex(key => key.id === idx + 1)]) : []
);
