import { ACTION_TYPES } from '../action-types';

export function getFirebaseDirectoryCollection(homePageSections) {
    return {
        type: ACTION_TYPES.GET_HOMEPAGE_SECTIONS_DB,
        payload: homePageSections
    }
}
