import React from 'react';
import { connect } from 'react-redux';
import { removeItemFromCart } from '../../redux/cart-action';
import './checkoutItem.scss';

function CheckoutItem({ removeItemFromCart, cartItem }) {
    const { imageUrl, name, quantity, price } = cartItem;
    return (
        <div className="checkoutItem">
            <div className="imgContainer">
                <img src={imageUrl} alt="item"/>
            </div>
            <span className="name">{name}</span>
            <span className="quantity">
                <div className="arrow">&#10094;</div>
                <span>{quantity}</span>
                <div className="arrow">&#10095;</div>
            </span>
            <span className="price">{price}</span>
            <div className="removeButton" onClick={() => removeItemFromCart(cartItem)}>&#10005;</div>
        </div>
    )
}

function mapDispatchToProps(dispatch) {
    return {
        removeItemFromCart: function(item) {
            return dispatch(removeItemFromCart(item))
        }
    }
}
export default connect(null, mapDispatchToProps)(CheckoutItem);
