import React from 'react';
import CollectionsOverview from './CollectionsOverview';
import CollectionPage from '../Collection-Page/CollectionPage';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';
import { fetchCollectionsStartAsync } from '../../redux/_db/collections-data/collections-data.actions';
import { createStructuredSelector } from 'reselect';
import { selectIsFetching, selectCollectionsIsLoaded } from '../../redux/_db/collections-data/collections-data.selector';
import withSpinner from '../HOCs/withSpinner';

const CollectionsOverviewWithSpinner = withSpinner(CollectionsOverview);
const CollectionPageWithSpinner      = withSpinner(CollectionPage);


// ShopPage gets collections from our firebase db
class ShopPage extends React.Component {

    componentDidMount() {
        const { fetchCollectionsStartAsync } = this.props;
        fetchCollectionsStartAsync();
    }

    render() {
        const { match, collectionIsFetching, collectionIsLoaded } = this.props;
        return (
            <div className="shopPageWrapper">
                <Route exact path={match.path}
                    render={props => <CollectionsOverviewWithSpinner loading={!collectionIsFetching} {...props}/>}
                />
                <Route path={`${match.path}/:collectionId`} 
                    render={props => <CollectionPageWithSpinner loading={!collectionIsLoaded} {...props}/>}
                />
            </div>
        )
    }
}

const mapStateToProps = createStructuredSelector(
    {
        collectionIsFetching: selectIsFetching,
        collectionIsLoaded: selectCollectionsIsLoaded
    }
)

function mapDispatchToProps(dispatch) {
    return {
        fetchCollectionsStartAsync: function() {
            return dispatch(fetchCollectionsStartAsync());
        } 
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
