import { takeLatest, put, all, call } from 'redux-saga/effects';
import { ACTION_TYPES } from '../action-types';
import { auth, provider, createUserProfileDoc, getCurrentUser } from '../../firebase/utils';
import { googleSignInSuccess, 
    googleSignInFailure, 
    emailSignInFailure,
    emailSignInSuccess,
    checkUserSession
 } from './user-actions';


// Google Sign In------------------------------------------------------------------------------------
function* onGoogleSignInStart() {
    yield takeLatest(
        ACTION_TYPES.SIGN_IN_START,
        signInWithGoogle
    );
}
function* signInWithGoogle() {
    try {
        const { user } = yield auth.signInWithPopup(provider);
        const userRef = yield call(createUserProfileDoc, user);
        const userSnapshot = yield userRef.get();
        yield put(
            googleSignInSuccess({id: userSnapshot.id, ...userSnapshot.data()})
        )
    } catch(error) {
        yield put(googleSignInFailure(error));
    }
}



// User Sign In------------------------------------------------------------------------------------
function* onEmailSignInStart() {
    yield takeLatest(
        ACTION_TYPES.SIGN_IN_START,
        signInWithEmail
    )
}
function* signInWithEmail(action) {
    try {
        const { email, password } = action.payload;
        const { user }            =  yield auth.signInWithEmailAndPassword(email, password);
        const userRef             = yield call(createUserProfileDoc, user);
        const userSnapshot        = yield userRef.get();
        yield put(
            emailSignInSuccess({id: userSnapshot.id, ...userSnapshot.data()})
        )
    } catch (error) {
        yield put(emailSignInFailure)
    }
}


// USER SESSION CHECKING---------------------------------------------------------------------------
function* onCheckUserSession() {
    yield takeLatest(
        ACTION_TYPES.CHECK_USER_SESSION,
        getCurrentUser
    )
}
function* isUserAuthenticated() {
    try {
        const userAuth = yield getCurrentUser();
        if (!userAuth) {
            return
        }

    } catch(error) {
        put(emailSignInFailure(error.message));
    }
}






export function* userSagas() {
    yield all([
        call(onCheckUserSession),
        call(onGoogleSignInStart),
        call(onEmailSignInStart)
    ]);
}
